#Now I want to shuffle the data within the lists randomly, each time recording how many streaks there are
#Making duplicates for the shuffles
OGeorgen=OGeorge.copy()
OHardenn=OHarden.copy()
OLeonardn=OLeonard.copy()
OThompsonn=OThompson.copy()
#Now the rest
Players=[OGeorgen,OHardenn,OLeonardn,OThompsonn]
MedianStreaks=[]
for i in range(len(Players)):
    n = 1 
    StreakList=[]
    while n <= 120:
        CurrentPlayer = Players[i]
        r.shuffle(CurrentPlayer)
        Streaks = StreakCount(Players[i])
        StreakList.append(Streaks)
        n += 1
    MedianStreaks.append(s.median(StreakList))
